import React, {useState} from 'react';
import {fbStore} from '../fbase';

const Nweet = (props) => {
  const [editing, setEditing] = useState(false);
  const [newNweet, setNewNweet] = useState(props.nweetObj.text);
  const onDeleteClick = async () => {
    const ok = window.confirm('Are you sure you want to delete this nweet?');
    if (ok) {
      await fbStore.deleteDoc(fbStore.doc(fbStore.getFirestore(), 'nweets', `${props.nweetObj.id}`));
    }
  };
  const toggleEditing = () => {
    setEditing((prev) => !prev);
  };
  const onSubmit = async (event) => {
    event.preventDefault();
    // console.log(newNweet);
    await fbStore.updateDoc(fbStore.doc(fbStore.getFirestore(), 'nweets', `${props.nweetObj.id}`), {text: newNweet});
    setEditing(false);
  };
  const onChange = (event) => {
    const {
      target: {value},
    } = event;
    setNewNweet(value);
  };
  return (
    <div>
      {editing ? (
        <>
          {props.isOwner && (
            <>
              <form onSubmit={onSubmit}>
                <input type="text" placeholder="Edit your nweet" value={newNweet} required onChange={onChange} />
                <input type="submit" value="Update Nweet" />
              </form>
              <button onClick={toggleEditing}>Cancel</button>
            </>
          )}
        </>
      ) : (
        <>
          <h4>{props.nweetObj.text}</h4>
          {props.nweetObj.attachmentUrl && <img src={props.nweetObj.attachmentUrl} alt="fig" width="50px" height="50px"></img>}
          {props.isOwner && (
            <>
              <button onClick={onDeleteClick}>Delete Nweet</button>
              <button onClick={toggleEditing}>Edit Nweet</button>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Nweet;
